/**
 * If the browser doesn't support WebAuthn, then set the value of the hidden
 * input field to false and submit the form. If the browser does support
 * WebAuthn, then set the value of the hidden input field to true and submit the
 * form
 *
 * @returns A boolean value representing WebAuthn API support.
 */
const webAuthnCheck = () => {
  if (!window.PublicKeyCredential) {
    document.getElementById('webAuthnSupport').value = false;
    document.getElementById('webAuthnSupportSubmitBtn').click();
    return false;
  }

  document.getElementById('webAuthnSupport').value = true;
  document.getElementById('webAuthnSupportSubmitBtn').click();
  return true;
};

/**
 * Wait for page to load to start the FIDO authentication process
 */
if (document.readyState === 'loading') {
  // Loading hasn't finished yet
  document.addEventListener('DOMContentLoaded', webAuthnCheck);
} else {
  // `DOMContentLoaded` has already fired
  const webAuthnSupportSubmitBtn = document.getElementById(
    'webAuthnSupportSubmitBtn'
  );
  if (webAuthnSupportSubmitBtn) {
    webAuthnSupportSubmitBtn.addEventListener('click', webAuthnCheck);
  }

  return webAuthnCheck();
}
