 *
 * FIDO Authentication
 *
 * Performs FIDO authentication using the WebAuthn API given public key
 * credentials option saved as a flow instance variable.
 *
 */

/**
 * If the string is valid JSON, parse it and return the object. If it's not
 * valid JSON, return the string
 *
 * @param strVal - The stringified JSON object that you want to parse.
 * @returns The parsed JSON as an object.
 */
const parseJSONIfNeeded = (strVal) => {
  try {
    return JSON.parse(strVal);
  } catch (error) {
    if (error.name === 'SyntaxError') {
      if (error.message === '"[object Object]" is not valid JSON') {
        // In most cases, this means that the method argument was given as a
        // JSON object, so we can just proceed.
        // But, you can add debugging here if you need to investigate further.
        return strVal;
      } else {
        // This means that we didn't get an object passed into the method as
        // an object and there's a syntax problem with it.
        throw new Error(
          'JSON parsing failed due to improper JSON syntax found in public key credential request options string.',
          {cause: error}
        );
      }
    } else {
      // This is unexpected and needs further debugging.
      throw new Error('Unexpected error. Please try again.', {
        cause: error,
      });
    }
  }
};

/**
 * It resets the error message element's text to an empty string
 */
const resetErrorMessageText = () => {
  const errorMsgElement = document.getElementById('errorMsg');
  if (errorMsgElement) {
    errorMsgElement.innerHTML = '';
  }
};

/**
 * Updates the error message element with passed in message
 *
 * @param message - The message to display in the error message element.
 * @returns True if message is updated, and false otherwise.
 */
const updateErrorMessageText = (message) => {
  const errorMsgElement = document.getElementById('errorMsg');

  if (errorMsgElement) {
    errorMsgElement.innerHTML = message;
    return true;
  }

  return false;
};

/**
 * It converts a list of credentials from the format given in the public key
 * credentials object to the format used by the WebAuthn API
 *
 * @param list - The list of credentials to convert.
 * @returns An array of credential objects which includes the type of
 * credential and its ID
 */
const credentialListConversion = (list) => {
  const credList = [];

  for (let i = 0; i < list.length; i++) {
    const cred = {
      type: list[i].type,
      id: new Uint8Array(list[i].id),
    };

    if (list[i].transports) {
      cred.transports = list[i].transports;
    }

    credList.push(cred);
  }

  return credList;
};

/**
 * It takes an object with the following properties:
 *
 * - challenge
 * - allowCredentials
 * - timeout
 *
 * And returns an object with the following properties:
 *
 * - challenge
 * - allowCredentials
 * - timeout
 *
 * The only difference is that the challenge property is converted from a base64
 * encoded string to a Uint8Array using @see {@link credentialListConversion}
 *
 * @param options - A public key credential request options object
 * @returns A publicKeyCredentialRequestOptions object with allowCredentials
 * converted into a Uint8Array.
 */
const buildPubKeyRequestOptions = (options) => {
  const publicKeyCredentialRequestOptions = {};

  if (options.challenge) {
    publicKeyCredentialRequestOptions.challenge = new Uint8Array(
      options.challenge
    );
  } else {
    throw new Error('Missing challenge from options');
  }

  if (options.allowCredentials) {
    publicKeyCredentialRequestOptions.allowCredentials =
      credentialListConversion(options.allowCredentials);
  } else {
    throw new Error('Missing allowCredentials from options');
  }

  if (options.timeout) {
    publicKeyCredentialRequestOptions.timeout = options.timeout;
  } else {
    throw new Error('Missing timeout from options');
  }

  return publicKeyCredentialRequestOptions;
};

/**
 * It converts a binary array to a base64 string
 *
 * @param bin - The binary data to convert to base64.
 * @returns A string of base64 encoded data.
 */
const toBase64Str = (bin) => {
  return btoa(String.fromCharCode.apply(null, new Uint8Array(bin)));
};

/**
 * It takes a FIDO assertion object and returns a public key credential object
 *
 * @param assertion - The FIDO assertion object returned from the authenticator.
 * @returns A [PublicKeyCredential]{@link https://www.w3.org/TR/webauthn-2/#iface-pkcredential} object
 */
const createPubliCKeyCredential = (assertion) => {
  const publicKeyCredential = {};
  const response = {};

  // Pull values from assertion to create public key credential
  if (assertion.id) {
    publicKeyCredential.id = assertion.id;
  }

  if (assertion.rawId) {
    publicKeyCredential.rawId = toBase64Str(assertion.rawId);
  }

  if (assertion.type) {
    publicKeyCredential.type = assertion.type;
  }

  // Create response object
  response.clientDataJSON = toBase64Str(assertion.response.clientDataJSON);
  response.authenticatorData = toBase64Str(
    assertion.response.authenticatorData
  );
  response.signature = toBase64Str(assertion.response.signature);
  response.userHandle = toBase64Str(assertion.response.userHandle);

  // Add response object to public key credential object
  publicKeyCredential.response = response;

  return publicKeyCredential;
};

/**
 * It takes the options that were sent from the server, builds a public key
 * credential request, and then uses the WebAuthn API to get a FIDO assertion
 *
 * @param options - The public key credential request options that are passed to the getFIDOAssertion function
 * @returns The FIDO assertion if authentication is successful
 */
const getFIDOAssertion = async (options) => {
  // Try to parse options in case they were sent in string form
  options = parseJSONIfNeeded(options);

  // Build the public key to pass into the WebAuthn API's
  // [navigator.credentials.get()]{@link https://www.w3.org/TR/webauthn-2/#sctn-getAssertion}
  // call using the passed in public key credential request options.
  const publicKeyCredentialRequestOptions = buildPubKeyRequestOptions(options);

  // Use WebAuthn API to get a FIDO assertion
  try {
    const assertion = await navigator.credentials.get({
      publicKey: publicKeyCredentialRequestOptions,
    });

    // Construct, in order to send to the server to validate, a
    // [PublicKeyCredential]{@link https://www.w3.org/TR/webauthn-2/#iface-pkcredential}
    // object to the server for verification and registration.
    const publicKeyCredential = createPubliCKeyCredential(assertion);

    // Sets the assertion value
    document.getElementById('assertionValue').value =
      JSON.stringify(publicKeyCredential);

    // Submit the assertion value and advance the flow
    document.getElementById('assertionButton').click();

    return JSON.stringify(publicKeyCredential);
  } catch (error) {
    // No acceptable authenticator or user refused consent. Cancel authentication
    throw new Error(
      'Failed to generate an assertion through FIDO authentication.',
      {
        cause: error,
      }
    );
  }
};

/**
 * It takes the public key credential request options from the flow instance
 * variable and uses them to request an assertion from the FIDO authenticator
 *
 * @param event - The event object that is passed to the function.
 * @returns The FIDO assertion object or false if FIDO
 * authentication failed
 */
const fidoAuthn = async (event) => {
  event?.preventDefault();

  const publicKeyCredentialRequestOptions =
    '{{global.variables.publicKeyCredentialRequestOptions}}';
  let assertion;

  resetErrorMessageText();

  if (publicKeyCredentialRequestOptions) {
    try {
      assertion = await getFIDOAssertion(publicKeyCredentialRequestOptions);
      return assertion;
    } catch (error) {
      updateErrorMessageText(error, {cause: error});
      return false;
    }
  } else {
    // Missing Public Key Credential request Options
    updateErrorMessageText('Missing credential request options');
  }

  return false;
};

/**
 * Wait for page to load to start the FIDO authentication process
 */
if (document.readyState === 'loading') {
  // Loading hasn't finished yet
  document.addEventListener('DOMContentLoaded', fidoAuthn);
} else {
  // `DOMContentLoaded` has already fired
  const fidoBtn = document.getElementById('fidoButton');
  if (fidoBtn) {
    fidoBtn.addEventListener('click', fidoAuthn);
  }

  return fidoAuthn();
}
